import React, { useEffect, useState } from 'react';
import Card from '../Components/Card/Card';
import {getVGCardsFromApi, getVGBoostersFromApi} from"../api/vanguardDB";
import { config } from '../config';


function Home(props) {

    const [cards, setCards] = useState([])
    const [boosters, setBoosters] = useState([])
    
    useEffect(() => {
        getVGCardsFromApi().then((res) => {
            console.log(res)
            setCards(res.result.cards)
        })
    }, []);  
    
    useEffect(() => {
        getVGBoostersFromApi().then((res) => {
            console.log(res)
            setBoosters(res.result.boosters)
        })
    }, []);

        return (                      
          
                    <main>

                    <h2>Nos derniers produits</h2>

                    {cards.map((card) => {
                        return(

                            <Card key={card.id}>

                                <h3>{card.name}</h3>
                                <img src={card.imageurlen} alt="illustration de la carte" />

                                <ul>
                                    <li>Clan: {card.clan}</li>
                                    <li>Nation: {card.nation}</li>
                                    <li>Grade: {card.grade}</li>
                                    <li>Type: {card.cardtype}</li>
                                    <li>Efet: {card.effect}</li>    
                                </ul>  

                            </Card>
                        )
                    })}

                    {boosters.map((booster) =>{
                        return(

                            <Card key={booster.id}>

                                <h3>{booster.name}</h3>
                                <img src={booster.imageurlen} alt="illustration du booster" />

                            <ul>
                                <li></li>
                                <li></li>
                                <li></li>
                                <li></li>
                                <li></li>
                            </ul>
                            </Card>
                        )
                    })}

                    </main>
                                   
        )
    }

export default Home
