import React, { useEffect, useState } from 'react';
import "./VgCards.css"
import Card from '../../Components/Card/Card';



function VgCards(props) {

    const [cards, setCards] = useState([])
    
    useEffect(() => {
        getVGCardsFromApi().then((res) => {
            console.log(res)
            setCards(res.result.cards)
        })
    }, []);  

    {cards.map((card) => {
        return(

            <main>
            <h2>Nos cartes à l"unité</h2>

            <Card key={card.id}>

                <h3>{card.name}</h3>
                <img src={card.imageurlen} alt="illustration de la carte" />

                <ul>
                    <li>Clan: {card.clan}</li>
                    <li>Nation: {card.nation}</li>
                    <li>Grade: {card.grade}</li>
                    <li>Type: {card.cardtype}</li>
                    <li>Efet: {card.effect}</li>    
                </ul>  

            </Card>
            </main>
        )
        
    })}

}

export default VgCards
