import axios from 'axios';
import { config } from '../config';
const cors=require("cors")

/*axios.request(cors({
    origin: 'http://localhost:3000',
})
)*/

export const getVGCardsFromApi = (data) => {
    console.log(data)
    return axios.get(`https://card-fight-vanguard-api.ue.r.appspot.com/api/v1/cards`, 
        {
            mode:'cors',
            credentials:'include'
            }
            ,data
        )
    .then(res=>{
        return res.data
    })
   
}

/*export const getAllCardsVG = () => {
    return axios.get(`${config.api_url}/api/v1/vgCards/all`)
}*/

/*export const getOneVgCard = () => {
    return axios.get(`${config.api_url}/api/v1/vgCards/one/:id`)
}*/

export const getVGBoostersFromApi = (data) => {
    console.log(data)
    return axios.get(`https://card-fight-vanguard-api.ue.r.appspot.com/api/v1/sets`,
        {
            mode:'cors',
            credentials:'include'
            
            
        }
        ,data
        )
    .then(res=>{
        return res.data
    })
}

export const getAllRecipes = (data) => {
    console.log(data)
    return axios.get(`${config.api_url}/api/v1/recipes/all`,data)
    .then((res) => {
        return res.data;
    })
    .catch((err) => {
        return err;
    })
}

export const deleteRecipes = (data) => {
    console.log(data)
    return axios.get(`${config.api_url}/api/v1/recipes/delete/:id`,data)
    .then((res) => {
        return res.data;
    })
    .catch((err) => {
        return err;
    })
}

export const editRecipes = (data) => {
    console.log(data)
    return axios.get(`${config.api_url}/api/v1/recipes/update/:id`,data)
    .then((res) => {
        return res.data;
    })
    .catch((err) => {
        return err;
    })
}

export const createRecipes = (data) => {
    console.log(data)
    return axios.get(`${config.api_url}/api/v1/recipes/add`,data)
    .then((res) => {
        return res.data;
    })
    .catch((err) => {
        return err;
    })
}

export const getAllComments = (data) => {
    console.log(data)
    return axios.get(`${config.api_url}/api/v1/recipes_comments/all`,data)
    .then((res) => {
        return res.data;
    })
    .catch((err) => {
        return err;
    })
}



export const deleteComments = (data) => {
    console.log(data)
    return axios.get(`${config.api_url}/api/v1/recipes_comments/delete/:id`,data)
    .then((res) => {
        return res.data;
    })
    .catch((err) => {
        return err;
    })
}

export const editComments = (data) => {
    console.log(data)
    return axios.get(`${config.api_url}/api/v1/recipes_comments/update/:id`,data)
    .then((res) => {
        return res.data;
    })
    .catch((err) => {
        return err;
    })
}

export const createComments = (data) => {
    console.log(data)
    return axios.get(`${config.api_url}/api/v1/recipes_comments/add`,data)
    .then((res) => {
        return res.data;
    })
    .catch((err) => {
        return err;
    })
}
