import React, {useState} from 'react';

import { Link } from 'react-router-dom';
import './Header.css'



function HamburgerHeader(props){
    console.log(props);

    /*const { cart } = useSelector((state) => ({
        ...state.cartReducer,
    }))*/

    

    const [toggleMenuVG, setToggleMenuVG] = useState(false)
    const [toggleNav, setToggleNav] = useState(false)

    const [text, setText] = useState("")
    const [VGCardsFromApi, setVGCardsFromApi] = useState([])

    const toggleNavbar = () => {
        setToggleNav(!toggleNav);
    }

    const getVGCardsFromApi= (e) => {
        e.preventDefault()
        setText(e.currentTarget.value)
        if(text.length > 0){
            getVGCardsFromApi(text)
            .then(res=>{
                setVGCardsFromApi(res.results);
            })
            
        }
    }

    const getVGCardsByKeyword = (e) => {
        e.preventDefault()
        setText(e.currentTarget.value)
        if(text.length > 0){
            getVGCardsFromApi(text)
            .then(res=>{
                setVGCardsFromApi(res.results);
            })
        }
	};

    

    return(
        
                    <nav className='hamburger'>

                            <Link to={"Home/"} className='Burger'>Acceuil</Link>

                            <h2 className='Burger'
                            onClick= {() => setToggleMenuVG(!toggleMenuVG)}
                            
                            >CardFight!! Vanguard
                            {toggleMenuVG && 
                
                (

                    <ul className='scrollingListVg'>

                        <li className='Burger'><Link to={"VgCards/"} className='Burger'>Carte Ã  l'unitÃ©</Link></li>
                        <li className='Burger'><Link to={"VgBoosters/"} className='Burger'>Boosters</Link></li>
                        <li className='Burger'><Link to={"Decks/"} className='Burger'>Recettes de deck</Link></li>

                    </ul>
                )}
                            </h2>                                                                                     
                         

                                                         
                                                
                        {props.isLogged ?

                            <>
                            {props.user.role=="admin" ?
                            
                                <>
                                    <Link onClick={toggleNavbar} to={"/dashboard"} className="Burger" >
                                        
                                        Page de profil
                                    </Link>

                                    <Link onClick={toggleNavbar} to={"/logout"} className="Burger"  >
                                        DÃ©connexion
                                    </Link>

                                    <Link onClick={toggleNavbar} to={"/Admin"} className='Burger'>
                                        Admin
                                    </Link>

                                </>

                                :

                                <>
                                    <Link onClick={toggleNavbar} to={"/dashboard"} className="Burger" >
                                        
                                        Page de profil
                                    </Link>

                                    <Link onClick={toggleNavbar} to={"/logout"} className="Burger"  >
                                        DÃ©connexion
                                    </Link>

                                </>
                                
                                }
                            </>
                            
                                :

                                <>
                                <Link onClick={toggleNavbar} to={'Connexion/'} className='Burger'> Connexion</Link>

                                <Link to={"Inscription/"} className='Burger'> Inscription</Link>  

                                </>
                        }

                        <form className='Burger'>
                            <label>Rechercher</label>
                            <input type="text"
                            value={text}
                            onChange={getVGCardsByKeyword}
                            ></input>
                        </form>

                        <Link to={'/cart'} className='Burger' > Panier </Link> 
                        
                                                                                                                                                         
                          
                    </nav>

                

        
    )

        
}

export default HamburgerHeader
