import React, { useEffect, useState } from 'react';
import Card from '../Components/Card/Card';
import {getAllVGCards} from"../api/vanguardDB";
import { Link } from 'react-router-dom';

//Il s'avère malheureusement que l'api, si elle fait un superbe boulôt pour les cartes, a fait le minimum pour les boosters, on les oublie finalement...

function Home() {

    const [cards, setCards] = useState([

    ])
    
    
    useEffect(() => {
        getAllVGCards().then((res) => {
            console.log(res)
            setCards(res.results)
        })
    }, []);  

        return (                      
          <>
            {console.log(cards)}
                    <main className='body'>

                    <h2>Nos derniers produits</h2>
                    
                                      
                    {

                    cards.length > 0 && cards.length < 20 ?

                    cards.map((card) => { //on boucle sur chaque carte
                        
                        return(
                            //La clé pour repérer la carte à chaque itération
                            //puis on affiche les champs qui nous intéressent de l'API
                            <Card key={card.id}> 
                                
                                <Link to={"/VgCardsDetail/:id"}>{card.name}</Link>
                                <img src={card.imageurlen} alt="illustration de la carte" />

                                <ul className='cardsList'>
                                    <li>Clan: {card.data.clan}</li>
                                    <li>Nation: {card.data.nation}</li>
                                    <li>Grade: {card.data.grade}</li>
                                    <li>Type: {card.data.cardtype}</li>
                                    <li>Efet: {card.data.effect}</li>    
                                </ul>  
                                
                            </Card>
                        )
                    })

                    : <h3>Aucuns résultats</h3>
                    
                    }


                    </main>
            </>
                                   
        )
    }

export default Home
