import React, {useEffect} from 'react'
import { addToCart } from '../../redux/actions/cartAction'
import { useDispatch} from "react-redux"
import './card-module.css'
import { config } from '../../config'
import { Link } from 'react-router-dom'


import styles from './card-module.css'
import { useState } from 'react'

function Card(props) {

    const [min, setMin] = useState(0);
    const [max, setMax] = useState(20);
    const [cardsList, setCardsList] = useState([])
    const [isLoading, setIsLoading] = useState(true);

    const [quantity, setQuantity] = useState("");
	const [error, setError] = useState(null);

    const dispatch = useDispatch()

   
   const onClickAddCart = (cards) => {
    console.log(cards);
    console.log(quantity);
    if(quantity !== "" && !isNaN(quantity)) {
        // setIsPopUp( true)
        dispatch(addToCart(props.cartInfos, cards, quantity))
    } else {
        setError({error: "Entrez une valeur correcte (chiffre)"})
    }
    setQuantity("")
}


    return (
        <article className='card'>

            <Link to={"VgcardsDetail/"}>

                <h3>{props.cardsInfos.name}</h3>
                <img src={config.img_url + props.cardsInfos.card_image} alt="" />
                <ul className='cardUl'>

                    <li className='cardLi'>
                    {props.cardsInfos.grade} 
                    </li>

                    <li className='cardLi'>
                    {props.cardsInfos.nation} 
                    </li>

                    <li className='cardLi'>
                    {props.cardsInfos.clan} 
                    </li>

                    <li className='cardLi'>
                    {props.cardsInfos.format} 
                    </li>

                    <li className='cardLi'>
                    {props.cardsInfos.rarity} 
                    </li>

                    <li className='cardLi'>
                    {props.cardsInfos.effects} 
                    </li>

                </ul>

                <p className='price'>
                    {props.cardsInfos.price}
                </p>

            </Link>

        </article>
    )
}

export default Card
