import React, { useEffect, useState } from 'react';
import Card from '../Components/Card/Card';
import {getVGCardsFromApi} from"../api/vanguardDB";
import { config } from '../config';
import { Link } from 'react-router-dom';

//Il s'avère malheureusement que l'api, si elle fait un superbe boulôt pour les cartes, a fait le minimum pour les boosters, on les oublie finalement...

function Home(props) {

    const [cards, setCards] = useState([])
    
    
    useEffect(() => {
        getVGCardsFromApi().then((res) => {
            console.log(res)
            setCards(res.result.cards)
        })
    }, []);  
    
    

        return (                      
          
                    <main className='body'>

                    <h2>Nos derniers produits</h2>

                    <p className='testContenuHome'>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Sint unde impedit natus minima est totam odit dolores! Dolores nesciunt ducimus enim, laudantium suscipit in delectus quibusdam nulla hic dolor eaque!</p>

                    <p className="testContenuHome">Lorem ipsum dolor, sit amet consectetur adipisicing elit. Exercitationem enim quaerat necessitatibus temporibus. Maiores, consequuntur quas consectetur illo provident, tenetur, ut aut saepe nemo fugit perferendis hic laboriosam? Nesciunt, consequatur.</p>
                    {cards.map((card) => {
                        return(

                            <Card key={card.data.id}>

                                <Link to={"/VgCardsDetail/:id"}>{card.data.name}</Link>
                                <img src={card.data.imageurlen} alt="illustration de la carte" />

                                <ul>
                                    <li>Clan: {card.data.clan}</li>
                                    <li>Nation: {card.data.nation}</li>
                                    <li>Grade: {card.data.grade}</li>
                                    <li>Type: {card.data.cardtype}</li>
                                    <li>Efet: {card.data.effect}</li>    
                                </ul>  

                            </Card>
                        )
                    })}


                    </main>
                                   
        )
    }

export default Home
