import React, { useEffect, useState } from 'react';
import Card from '../Components/Card/Card';
import cardsReducer from '../redux/reducers/cardsReducer';
import {getAllVgCards} from"../redux/actions/cardsAction"
import { useDispatch } from 'react-redux';
import {getAllCardsVG} from "../api/vanguardDB";

function Home(props) {

    const [cards, setCards] = useState([])

    useEffect(() => {
        getAllCardsVG().then((res) => {
            console.log(res);

            setCards(res.result.cards)
        })
    }, []);


    return (                      

            
                <main>

                 <h2>Nos derniers produits</h2> 

                 {cards.map((card)=>{
                     <Card>
                        <h3>{card.name}</h3> 
                     </Card>
                 })}
                 
                 

                </main>
                    
        
    )
}

export default Home
