import React, {useState, useEffect} from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { getVGCardsAndBoosters } from '../../api/vanguardDB';

import './Header.css'



function Header(props){
    console.log(props);

    /*const { cart } = useSelector((state) => ({
        ...state.cartReducer,
    }))*/

    const [toggleMenuVG, setToggleMenuVG] = useState(false)
    const [toggleNav, setToggleNav] = useState(false)

    const [text, setText] = useState("")
    const [VGCardsAndBoosters, setVGCardsAndBoosters] = useState([])

    const toggleNavbar = () => {
        setToggleNav(!toggleNav);
    }

    const getVGCardsAndBoostersByKeyword = (e) => {
        e.preventDefault()
        setText(e.currentTarget.value)
        if(text.length > 0){
            getVGCardsAndBoosters(text)
            .then(res=>{
                setVGCardsAndBoosters(res.results);
            })
            
        }
    }

    

    return(
        <header>
            {console.log(VGCardsAndBoosters)}
                <h1 className='headerH1'> Carte Age</h1>
                    <nav className='headerNav'>

                            <Link to={"Home/"} className='link'>Acceuil</Link>

                            <h2 className='scrollNav'
                            onClick= {() => setToggleMenuVG(!toggleMenuVG)}
                            
                            >CardFight!! Vanguard</h2>                                                                                     
                         

                            <Link to={"inscription/"} className='link'> Inscription</Link>                               
                                                
                        {props.isLogged ?
                            <>
                                <Link onClick={toggleNavbar} to={"/dashboard"} className="dashBoard" >
									
									Page de profil
								</Link>

								<Link onClick={toggleNavbar} to={"/logout"} className="/logout"  >
									DÃ©connexion
								</Link>

                            </>
                            :
                            <Link onClick={toggleNavbar} to={'connexion/'} className='link'> Connexion</Link>
                        }

                        <form className='search'>
                            <label>Rechercher</label>
                            <input type="text"
                            value={text}
                            onChange={getVGCardsAndBoostersByKeyword}
                            ></input>
                        </form>

                        <Link to={'/cart'} className='link' > Panier </Link> 
                        
                                                                                                                                                         
                          
                    </nav>

                {toggleMenuVG && 
                
                (

                    <ul className='scrollingListVg'>

                        <li className='vgLi'><a href='#' className='vgLiA'>Carte Ã  l'unitÃ©</a></li>
                        <li className='vgLi'><Link to={"vgBoosters/"} className='vgLiA'>Boosters</Link></li>
                        <li className='vgLi'><a href='#' className='vgLiA'>Recettes de deck</a></li>

                    </ul>
                )}

        </header>
    )

        
}

export default Header
