import { MODIFY_CART } from "../actions/actions-types";

const initialState = {
	cart: [],
};

export default function cartReducer(state = initialState, action) {
	switch (action.type) {
		case MODIFY_CART:
			return { ...state, cart: action.payload };

		default:
			break;
	}

	return state;
}
