import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import './Header.css'



function Header(){

    const [toggleMenuVG, setToggleMenuVG] = useState(false)

    

    return(
        <header>
                <h1 className='headerH1'> Carte Age</h1>
                    <nav className='headerNav'>

                        <section>

                            <h2 className='scrollNav'
                            onMouseOver= {() => setToggleMenuVG(true)}
                            onMouseOut = {() => setToggleMenuVG(false)}
                            
                            >CardFight!! Vanguard</h2>                                                              

                        </section>
                                
                                <h2 className='scrollNav' >Test css inscription</h2>                               
                                <h2 className='scrollNav'>Test css connexion</h2>
                                                     
                    </nav>

                {toggleMenuVG && (
                    <ul className='scrollingListVg'>
                        <li><a href='#'>Carte à l'unité</a></li>
                        <li><a href='#'>Boosters</a></li>
                        <li><a href='#'>Recettes de deck</a></li>
                        </ul>
                )}
        </header>
    )

        
}

export default Header
