import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import vgBoostersDetail from '../../Container/VgBoostersDetail';
import './Header.css'



function Header(){

    const [toggleMenuVG, setToggleMenuVG] = useState(false)

    

    return(
        <header>
                <h1 className='headerH1'> Carte Age</h1>
                    <nav className='headerNav'>

                        

                            <h2 className='scrollNav'
                            onClick= {() => setToggleMenuVG(!toggleMenuVG)}
                            
                            >CardFight!! Vanguard</h2>                                                                                     
                         

                            <a className='inscription' href='#' > Inscription</a>                               
                                                

                            <a className='connexion' href="#"> Connexion</a>
                            
                         
                          
                    </nav>

                {toggleMenuVG && 
                
                (

                    <ul className='scrollingListVg'>

                        <li className='vgLi'><a href='#' className='vgLiA'>Carte à l'unité</a></li>
                        <li className='vgLi'><Link to ={"vgBoosters/"}>Boosters</Link></li>
                        <li className='vgLi'><a href='#' className='vgLiA'>Recettes de deck</a></li>

                    </ul>
                )}

        </header>
    )

        
}

export default Header
