import axios from 'axios';
import { config } from '../config';
const cors=require("cors")

/*axios.request(cors({
    origin: 'http://localhost:3000',
})
)*/

export const getVGCardsFromApi = (keywords) => {
    console.log(keywords)
    return axios.get(`https://card-fight-vanguard-api.ue.r.appspot.com/api/v1/cards`, 
    {
        mode:'cors',
        credentials:'include'
    })
    .then(res=>{
        return res.data
    })
   
}

/*export const getAllCardsVG = () => {
    return axios.get(`${config.api_url}/api/v1/vgCards/all`)
}*/

/*export const getOneVgCard = () => {
