}*/

export const getVGBoostersFromApi = (keywords) => {
    console.log(keywords)
    return axios.get(`https://card-fight-vanguard-api.ue.r.appspot.com/api/v1/sets`,
    {
        headers: {
            authorization:'Access-Control-Allow-Origin' ,
            'Content-type': 'application/json',
        }
    })
    .then(res=>{
        return res.data
    })
}

export const getAllRecipes = (keywords) => {
    console.log(keywords)
    return axios.get(`${config.api_url}/api/v1/recipes/all`)
    .then((res) => {
        return res.data;
    })
    .catch((err) => {
        return err;
    })
}

export const getAllComments = (keywords) => {
    console.log(keywords)
    return axios.get(`${config.api_url}/api/v1/recipes_comments/all`)
    .then((res) => {
        return res.data;
    })
    .catch((err) => {
        return err;
    })
}
